def find_mentors(char, string, index, str_len):
    rtn = 0
    iter = 0
    i = index - 1000
    neg = 0
    if (i < 0):
        neg = i
        i = 0
    if char == 'a':
        while iter < 2001 + neg and i < str_len:
            if (string[i] == 'A'):
                rtn += 1
            iter += 1
            i += 1
    elif char == 'b':
        while iter < 2001 + neg and i < str_len:
            if (string[i] == 'B'):
                rtn += 1
            iter += 1
            i += 1
    elif char == 'c':
        while iter < 2001 + neg and i < str_len:
            if (string[i] == 'C'):
                rtn += 1
            iter += 1
            i += 1
#    print(rtn)
    return (rtn)

string = ""

i = 0
app = ''
while i < 1000:
    app = ''.join([app, string])
    i += 1

j = 0
b = 0
y = 0

str_len = len(app)
i = 0
while (i < str_len):
    if app[i] == 'a':
        j += find_mentors('a', app, i, str_len)
    if app[i] == 'b':
        b += find_mentors('b', app, i, str_len)
    if app[i] == 'c':
        y += find_mentors('c', app, i, str_len)
    i += 1

print ("total:", j + b + y)
